from collections import Counter

potions_need = {"A": 0, "B": 1, "C": 3, "D": 5, "x": 0}


def solution_1(lines: list[str]):
    c = Counter(lines[0])

    return c["B"] + 3 * c["C"]


def solution_2(lines: list[str]):
    input = ""
    # input = "AxBCDDCAxD"
    pairs = [input[i : i + 2] for i in range(0, len(input), 2)]

    potions = 0
    for pair in pairs:
        additional = 1
        if "x" in pair:
            additional = 0

        for c in pair:
            potions += potions_need[c] + additional

    return potions


def solution_3(lines: list[str]):
    input = ""
    # input = "xBxAAABCDxCC"
    trios = [input[i : i + 3] for i in range(0, len(input), 3)]

    potions = 0
    for trio in trios:
        additional = 2 - trio.count("x")
        for c in trio:
            if c == "x":
                continue
            potions += potions_need[c] + additional

    return potions
